TsvService.loadingSpinner = function () {
	console.log("loadingSpinner()");
	throw new Error('I need to be a React component!');

	var spinnerColor = _RootscopeStore2.default.getCache('custommachinesettings.spinnerColor', '255,255,255');

	var canvas = document.getElementById('spinner');
	var context = canvas.getContext('2d');
	canvas.width = 200;
	canvas.height = 200;
	canvas.style.width = '200px';
	canvas.style.height = '200px';

	var start = new Date();
	var lines = 16,
	    cW = canvas.width,
	    cH = canvas.height;

	var draw = function draw() {
		var rotation = parseInt((new Date() - start) / 1000 * lines) / lines;
		context.save();
		context.clearRect(0, 0, cW, cH);
		context.translate(cW / 2, cH / 2);
		context.rotate(Math.PI * 2 * rotation);

		for (var i = 0; i < lines; i++) {
			context.beginPath();
			context.rotate(Math.PI * 2 / lines);
			context.moveTo(cW / 10, 0);
			context.lineTo(cW / 4, 0);
			context.lineWidth = cW / 30;
			context.strokeStyle = "rgba(" + spinnerColor + "," + i / lines + ")";
			context.stroke();
		}
		context.restore();
	};
	window.setInterval(draw, 1000 / 30);
};

